// COMMON
body {
  font-size: 14px;
  font-family: 'Nanum Gothic', sans-serif;
  line-height: 1.4;
  font-weight: 400;
  word-spacing: 10;
  background-color: palegoldenrod;
}
a {
  text-decoration: none;
  color: #fff;
}
img {
  display: block;
  background-repeat: no-repeat;
}
.inner {
  width: 1100px;
  margin: 0 auto;
  position: relative;
}

// HEADER
header {
  background-color: #000;
  border-bottom: 1px solid #b3b3b3;
  .inner {
    height: 90px;
    display: flex;
  }
  .left-sub-menu {
    ul.menu {
      display: flex;
      li {
        padding: 10px 10px;
        &:first-child {
          padding-left: 0px;
        }
      }
    }
  }

  .left-main-menu {
    position: absolute;
    top: 0;
    bottom: 0;
    height: 0;
    margin: auto 0;
    ul.menu {
      display: flex;
      > li.item {
        position: relative;
        display: inline-block;
        margin-right: 60px;
        &:first-child {
          margin: 0 10px;
        }
        &:nth-child(2) {
          margin-left: 15px;
          & ~ li.item {
            > .item__contents {
              position: relative;
              display: none;
              &::before {
                content: "";
                position: fixed;
                width: 100%;
                height: 30px;
                left: 0;
                top: 91px;
                background-color: rgba(0, 0, 0, 0.8);
              }
              &::after {
                content: "";
                position: absolute;
                top: -7px;
                width: 35px;
                height: 3px;
                background-color: #fff;
              }
              .contents__menu {
                position: absolute;
                width: 500px;
                display: flex;
                li {
                  a {
                    position: relative;
                    margin: 0 10px;
                    &:hover::after {
                      content: "";
                      position: absolute;
                      bottom: 0;
                      left: 0;
                      width: 100%;
                      height: 1px;
                      background-color: #fff;
                    }
                  }
                }
              }
            }
            &:hover .item__contents {
              display: block;
            }
          }
        }
        &:nth-child(3) {
          margin-left: 50px;
        }
        .item__name {
          display: inline-block;
          height: 46px;
          cursor: pointer; 
        }
      }      
    }
  }
  @at-root .left-main-menu ul.menu li.item:nth-child(3) > .item__contents > ul.contents__menu {
    margin-left: -135px;
  }
  @at-root .left-main-menu ul.menu li.item:nth-child(4) > .item__contents > ul.contents__menu {
    margin-left: -105px;
  }
  @at-root .left-main-menu ul.menu li.item:nth-child(5) > .item__contents > ul.contents__menu {
    margin-left: -65px;
  }


  .logo {
    position: absolute;
    width: 140px;
    height: 47px;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    margin: auto;
    padding: 10px 0;
  }

  .right-sub-menu {
    position: absolute;
    right: 0;
    ul.menu {
      display: flex;
      li {
        padding: 10px 10px;
        &:last-child {
          padding-right: 0;
        }
      }
    }
  }
  .right-main-menu {
    position: absolute;
    right: 0;
    top: 0;
    bottom: 0;
    height: 0;
    margin: auto 0;
    ul.menu {
      display: flex;
      > li.item {
        position: relative;
        display: inline-block;
        margin-right: 60px;
        &:nth-child(1) > .item__contents {
          position: relative;
          display: none;
          .contents__menu {
            position: absolute;
            width: 500px;
            display: flex;
          }
          &::before {
            content: "";
            position: fixed;
            width: 100%;
            height: 30px;
            left: 0;
            top: 91px;
            background-color: rgba(0, 0, 0, 0.8);
          }
          &::after {
            content: "";
            position: absolute;
            top: -7px;
            width: 35px;
            height: 3px;
            background-color: #fff;
          }
        }
        &:nth-child(3) {
          margin-right: 85px;
          & > .item__contents {
            position: relative;
            display: none;
            .contents__menu {
              position: absolute;
              width: 500px;
              display: flex;
            }
            &::before {
              content: "";
              position: fixed;
              width: 100%;
              height: 30px;
              left: 0;
              top: 91px;
              background-color: rgba(0, 0, 0, 0.8);
            }
            &::after {
              content: "";
              position: absolute;
              top: -7px;
              width: 35px;
              height: 3px;
              background-color: #fff;
            }
          }
        }
        &:nth-child(4) {
          margin: 0 15px;
        }
        &:last-child {
          margin: 0 10px;
        }
        .item__name {
          display: inline-block;
          height: 46px;
          cursor: pointer;
        }
        .item__contents {
          display: block;
          li a {
            position: relative;
            margin: 0 10px;
            &:hover::after {
              content: "";
              position: absolute;
              bottom: 0;
              left: 0;
              width: 100%;
              height: 1px;
              background-color: #fff;
            }
          }
        }
        &:hover .item__contents {
          display: block;
        }
      }
    }
  }
}  
@at-root .right-main-menu ul.menu li.item:nth-child(1) > .item__contents > ul.contents__menu {
  margin-left: -135px;
}
@at-root .right-main-menu ul.menu li.item:nth-child(3) > .item__contents > ul.contents__menu {
  margin-left: -105px;
}

@at-root .left-main-menu ul.menu li.item:nth-child(1) > .closed {
  position: absolute;
  top: -6px;
  left: -3px;
  width: 30px;
  height: 30px;
  background-color: #333;
  border-radius: 20px;  
  display: block;
  cursor: pointer;
}
@at-root .left-main-menu ul.menu li.item:nth-child(1) > .closed > img {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
}
